import { useState, useEffect } from 'react'
import NewsReel from '../../../components/news-reel'
import * as S from './NewsReel.styled'

const NewsReelContainer = () => {
    // const [current, setCurrent] = useState(0)
    const [articles, setArticles] = useState([])

    // useEffect(() => {
    //     const next = (current + 1) % landingGifUrls.length
    //     const id = setTimeout(() => setCurrent(next), 6000)
    //     return () => clearTimeout(id)
    // }, [current])

    // Fetch web scraper api to get current headlines
    useEffect(() => {
        fetch('http://localhost:4000/')
            .then(res => res.json())
            .then(articleArr => {
                setArticles(articleArr)
            })
    }, [])

    return <S.NewsReelContainer>
        <NewsReel articles={articles} />
    </S.NewsReelContainer>
}

export default NewsReelContainer
