import styled from 'styled-components'

export const NewsReel = styled.div`
	transform-style: preserve-3d;
`

export const Article = styled.div`
    opacity: 0;
    position: absolute;
    top:0;
    width: 100%;
    margin: auto;
    padding: 1rem 4rem;
    z-index: 100;
    transition: transform .5s, opacity .5s, z-index .5s;
`

export const ArticleInitial = styled.div`
    opacity: 1;
    position: relative;
    z-index: 900;
`

export const ArticleActive = styled.div`
    opacity: 1;
    position: relative;
    z-index: 900;
`

export const ArticlePrev = styled.div`
    z-index: 800;
    transform: translateX(-100%);
`

export const ArticleNext = styled.div`
    z-index: 800;
    transform: translateX(100%);
`

export const ButtonPrev = styled.div`
    position: absolute;
    top:50%;
    width: 3rem;
    height: 3rem;
    background-color: #FFF;
    transform: translateY(-50%);
    border-radius: 50%;
    cursor: pointer; 
    z-index: 1001;
    border: 1px solid black;
`

export const ButtonNext = styled.div`
    position: absolute;
    top:50%;
    width: 3rem;
    height: 3rem;
    background-color: #FFF;
    transform: translateY(-50%);
    border-radius: 50%;
    cursor: pointer; 
    z-index: 1001;
    border: 1px solid black;
`


