import React, { useState, useEffect } from 'react'
import * as S from './NewsReel.styled'

const NewsReel = (props) => {
    const [current, setCurrent] = useState(0)

    useEffect(() => {
        const next = (current + 1) % props.articles.length

        const id = setTimeout(() => setCurrent(next), 2000)
        return () => clearTimeout(id)
    }, [current])

    // const listArticles = () => {
    //     return props.articles.map((article, i) => {
    //         if (i === 0) {
    //             return <S.ArticleInitial>
    //                 {article.title}
    //             </S.ArticleInitial>
    //         } else {
    //             return <S.Article>
    //                 {article.title}
    //             </S.Article>
    //         }
    //     })
    // }

    if (props.articles.length === 0) {
        return null
    } else {
        return <S.NewsReel>
            <a href={props.articles[current].url}>{props.articles[current].title}</a>
        </S.NewsReel>
    }
}

export default NewsReel
