import { useState, useEffect } from 'react'
import Router from 'next/router'
import * as S from './Landing.styled'
import Button from '../../../components/button'
import TabBar from '../../../components/tab-bar'
import NewsReel from '../../../components/news-reel'

const Landing = () => {
	const [current, setCurrent] = useState(0)
	const landingGifUrls = [
		'/static/gifs/financial-business-chart.gif',
		'/static/gifs/new-york-cityscape.gif',
		'/static/gifs/financial-data-and-graphs.gif'
	]

	useEffect(() => {
		const next = (current + 1) % landingGifUrls.length
		const id = setTimeout(() => setCurrent(next), 6000)
		return () => clearTimeout(id)
	}, [current])

	// Fetch web scraper api to get current headlines
	let articles
	useEffect(() => {
		fetch('http://localhost:4000/')
			.then(res => res.json())
			.then(articleArr => {
				articles = articleArr
			})
	}, [])

	return <S.LandingWrapper style={{ backgroundImage: `url("${landingGifUrls[current]}")` }}>
		<S.ContentWrapper>
			<TabBar />
			<S.Title>
				Industry research by
				<br /> industry experts.
			</S.Title>
			<S.Description>
				We are a boutique consulting firm serving the financial information
				services industry. Our consultants are industry experts on market and
				reference data products and investment management solutions.
			</S.Description>
			<S.ButtonWrapper>
				<Button
					btnText="View Research"
					onClick={() => Router.push('/research')}
				/>
				<Button
					btnText="Contact us"
					isDefault={false}
					onClick={() => Router.push('/contact')}
				/>
			</S.ButtonWrapper>
			<NewsReel articles={articles} />
		</S.ContentWrapper>
	</S.LandingWrapper>
}

export default Landing

