import App, { Container } from 'next/app'
import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import { ThemeProvider } from 'styled-components'
import theme from '../assets/theme'
import GlobalStyles from '../styles/GlobalStyles'
import { StoreProvider } from '../store/useStore'

import analyticsService from '../services/analyticsService'

export default class MyApp extends App {
	constructor(props) {
		super(props)
		this.state = { currentRoute: this.props.router.route }
		this._ref = React.createRef()
	}

	static async getInitialProps({ Component, router, ctx }) {
		let pageProps = {}

		if (Component.getInitialProps) {
			pageProps = await Component.getInitialProps(ctx)
		}

		return { pageProps }
	}

	componentDidUpdate() {
		if (this.state.currentRoute !== this.props.router.route) {
			this.setState(
				{
					currentRoute: this.props.router.route
				},
				() => {
					analyticsService.trackPage(this.state.currentRoute)
				}
			)
		}
	}

	/**
	 * Initializes GA if has not been initialized yet
	 */
	componentDidMount() {
		const { currentRoute } = this.state

		if (!window.GA_INITIALIZED) {
			analyticsService.initialize()
			window.GA_INITIALIZED = true
		}

		analyticsService.trackPage(currentRoute)

		const script = document.createElement('script')
		script.src = "https://s3.tradingview.com/external-embedding/embed-widget-ticker-tape.js"
		script.async = true
		script.innerHTML = JSON.stringify({
			"symbols": [
				{
					"proName": "FOREXCOM:SPXUSD",
					"title": "S&P 500"
				},
				{
					"proName": "FOREXCOM:NSXUSD",
					"title": "Nasdaq 100"
				},
				{
					"proName": "FX_IDC:EURUSD",
					"title": "EUR/USD"
				},
				{
					"proName": "BITSTAMP:BTCUSD",
					"title": "BTC/USD"
				},
				{
					"proName": "BITSTAMP:ETHUSD",
					"title": "ETH/USD"
				}
			],
			"showSymbolLogo": true,
			"colorTheme": "dark",
			"isTransparent": false,
			"displayMode": "adaptive",
			"locale": "en"
		})
		this._ref.current.appendChild(script)
	}

	render() {
		const { Component, pageProps } = this.props
		return (
			<Container>
				<StoreProvider>
					<GlobalStyles />
					<ThemeProvider theme={theme}>
						<div>
							<div className="tradingview-widget-container" ref={this._ref}>
								<div className="tradingview-widget-container__widget"></div>
								{/* <div className="tradingview-widget-copyright"><a href="https://www.tradingview.com" rel="noreferrer" target="_blank"><span class="blue-text">Ticker Tape</span></a> by TradingView</div> */}
							</div>
							<Component {...pageProps} />
						</div>
					</ThemeProvider>
				</StoreProvider>
			</Container>
		)
	}
}
