import styled from 'styled-components'

export const NewsReel = styled.div`
	transform-style: preserve-3d;
    height: 100%;
`

export const Article = styled.div`
    opacity: 0;
    position: absolute;
    top:0;
    width: 100%;
    margin: auto;
    padding: 1rem 4rem;
    z-index: 100;
    transition: transform .5s, opacity .5s, z-index .5s;
`

export const ArticleInitial = styled.div`
    opacity: 1;
    position: relative;
    z-index: 900;
`

export const ArticleActive = styled.div`
    opacity: 1;
    position: relative;
    z-index: 900;
`

export const ArticlePrev = styled.div`
    z-index: 800;
    transform: translateX(-100%);
`

export const ArticleNext = styled.div`
    z-index: 800;
    transform: translateX(100%);
`
