import styled from 'styled-components'
import breakpoint from 'styled-components-breakpoint'

export const LandingWrapper = styled.video`

	position: fixed;
	right: 0;
	bottom: 0;




`

// background-image: url('/static/images/mobile-landing.jpg');
// background-size: cover;
// height: 100vh;

// ${breakpoint('tablet')`
// 		background-image: url("/static/images/tablet-landing.jpg");
// 	`}

// ${breakpoint('desktop')`
// 		background-image: url("/static/images/desktop-landing-2.jpg");

//   `}

export const ContentWrapper = styled.div`
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;

	display: flex;
	flex-direction: column;
	flex: 1;
`

export const Title = styled.h1`
	font-family: ${(props) => props.theme.fonts.graphikBold};
	font-size: 42px;
	line-height: 44px;
	color: ${(props) => props.theme.colors.white};
	padding: 200px 0 12px 0;
	margin-bottom: 16px;
	${breakpoint('desktop')`
    font-size: 57px;
    line-height: 64px;
  `}
`

export const Description = styled.h1`
	font-family: ${(props) => props.theme.fonts.financier};
	font-weight: 400;
	font-size: 21px;
	line-height: 24px;
	color: ${(props) => props.theme.colors.white};
	margin: 0 0 42px 0;
	text-align: left;
	max-width: 720px;
	${breakpoint('desktop')`
    font-size: 24px;
    line-height: 32px;
  `}
`

export const ButtonWrapper = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	max-width: 340px;
	margin: 0 0 38px 0;
	${breakpoint('desktop')`
    	max-width: 380px;
  `}
`
